import React, { useState } from "react";
import ExpenseDate from "../Expenses/ExpenseDate";
import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setEntertedTitle] = useState();
  const [enteredAmount, setEntertedAmount] = useState();
  const [enteredDate, setEntertedDate] = useState();

  // const [userInput, setUserInput]= useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: ''
  // });

  const titleChangeHandler = (event) => {
    setEntertedTitle(event.target.value);
    // setUserInput(
    //   {
    //     ...userInput,
    //     enteredTitle: event.target.value
    //   }
    // )
  };

  const amountChangeHandler = event => {
     setEntertedAmount(event.target.value)
    // setUserInput(
    //   {
    //     ...userInput,
    //     enteredAmount: event.target.value
    //   }
    // )
  };

  const dateChangeHandler = event => {
    setEntertedDate(event.target.value)
    // setUserInput(
    //   {
    //     ...userInput,
    //     enteredDate: event.target.value
    //   }
    // )
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const ExpenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate)
    };

    props.onSaveExpenseData(ExpenseData);
    setEntertedTitle('');
    setEntertedAmount('');
    setEntertedDate('');
  };

 return (
  <form onSubmit={submitHandler}>
    <div className="new-expense__controls">
      <div className="new-expense__control">
        <label> Title</label>
        <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
      </div>
      <div className="new-expense__control">
        <label> Amount</label>
        <input type="number" value={enteredAmount} min="0.01" step="0.01" onChange={amountChangeHandler}/>
      </div>
      <div className="new-expense__control">
        <label> Date</label>
        <input type="Date" value={enteredDate} min="2019-01-01" step="2022-12-31" onChange={dateChangeHandler}/>
      </div>
    </div>
    <div className="new-expense__actions">
      <button type="submit">Add Expense</button>
    </div>
 </form>
 )

}


export default ExpenseForm;
